                    $('#allChatCount').text(data.allChats);
                    $('#activeChatCount').text(data.activeChats);
                    $('#oldPendingChats').text(data.oldPendingChats);
                    $('#pendingChatCount').text(data.pendingChats);
                    $('#closedChatCount').text(data.closeChats);
                    $('#agentLoginTime').text(data.loginTime);
                    $('#agentReadyTime').text(data.readyTime);
                    $('#agentBreakTime').text(data.breakTime);
                    $('#agentidealTime').text(data.idealTime);
                    $('#softBreakTime').text(data.softTime);
                    if(data.campMsg!=''){
                        if(data.campMsg=='Show Campaigns'){
                            $('.campaign-container').show();
                        }else if(data.campMsg=='No Campaigns'){ 
                            alert("No campaign has been assigned to you. Please contact your administrator.");
                        }else{  

                        }
                        
                    }
                    google.charts.load("current", {packages:["corechart"]});
                    google.charts.setOnLoadCallback(drawChart);
                },
                error: function() {
                    // Handle errors if the AJAX request fails
                    $('#agentLoginTime').text('00:00:00');
                    $('#agentBreakTime').text('00:00:00');
                    $('#agentReadyTime').text('00:00:00');
                    $('#agentidealTime').text('00:00:00');
                    $('#softBreakTime').text('00:00:00');
                }
            });
        }

        // Call the function initially to load data when the page loads
        $(document).ready(function() {
            updateAgentMetrics(); // Fetch initial data
